// Prefetching example using React.lazy
const LazyPage = React.lazy(() => import('./LazyPage'));

export default function Home() {
  const prefetch = React.useRef();
  useEffect(() => {
    prefetch.current = React.lazy(() => import('./LazyPrefetch'));
